function priAndKeyComparator(testPri, testKey, valPri, valKey) {
  var x = 0;
  if( !_.isUndefined(testPri) ) {
    x = priorityComparator(testPri, valPri);
  }
  if( x === 0 && !_.isUndefined(testKey) && testKey !== valKey ) {
    x = testKey < valKey? -1 : 1;
  }
  return x;
}

